import friends from '../data/friends.json';
import { useState, useEffect } from "react";

const App = () => {
  // variables de estado//
  const [data, setData] = useState([]);
  const [filterC, setFilterC] = useState('all');
  const [searchPhrase, setSearchPhrase] = useState("");
  const [newPhrase, setNewPhrase] = useState({
    quote: "",
    character: ""
  });
  const handleChange = (ev) => {
    this.setState({ selectedValue: ev.target.value })


    const handleSearchPhrase = (ev) => {
      setSearchPhrase(ev.target.value);
    }
    const handleFilterCharacter = (ev) => {
      setFilterC(ev.target.value);
    };
    const handleNewPhrase = (ev) => {
      setNewPhrase({
        ...newPhrase,
        [ev.target.id]: ev.target.value
      });
    }
    const handleClick = (ev) => {
      ev.preventDefault();
      setData([...data, newPhrase])
      setNewPhrase({
      });
    }

    useEffect(() => {
      friends().then((data) => {
        setData(data);
      });
    }, []);

    //render//

    const htmlData = data
      .filter((item) => {
        return item.quote.toLowerCase().includes(searchPhrase.toLowerCase());
      })
      .map((item, index) => {
        return (
          <li className='phrase_person' key={index}>
            <p className='phrase'>{item.quote} </p>
            <p className='person'>{item.character} </p>
          </li>
        )
      });

  }
  return (
    <div className="page">
      <form action="">
        filtrar por frase
        <input type="text"
          value={filterC}
          onChange={handleSearchPhrase} />
      </form>
      <form action="">
        filtrar por personaje
        <select onChange={handleChange}
          value={handleFilterCharacter}>
          <option>todos</option>
          <option>Ross</option>
          <option>Monica</option>
          <option>Joey</option>
          <option>Phoebe</option>
          <option>Chandler</option>
          <option>Rachel</option>
        </select>
      </form>
      <ul>{htmlData}</ul>
      <form action="">
        <p> Añadir nueva frase </p>
        frase
        <input
          type="text"
          id="quote"
          value={newPhrase.quote}
          onChange={handleNewPhrase}
        />
        <br />
        personaje
        <input
          type="text"
          id="character"
          value={newPhrase.character}
          onChange={handleNewPhrase}
        />
        <br />
        <button
          onClick={handleClick}
        >Añadir nueva frase:</button>
      </form>

    </div>
  );
}

export default App;
